INSERT INTO `data` VALUES(70, 'trungnhan', 'Bạn nào giỏi Pascal vào giúp mình với :((( mình cần gấp :((?', 'viết chương trình nhập than khai thác trong tháng 11/2015 cho 30 công trường của mỏ than A, biết mỗi công trường khai thác không dưới 2500 tấn. Tính tổng và trung bình số lượng than khai thác được, đưa ra công trường có số lượng than thấp nhất, cao nhất. in kết quả ra màn hình<br />\r\n<br />\r\nTrả lời :<br />\r\nuses crt; <br />\r\nvar a:array[1..30] of integer; <br />\r\nmin,max,i,n,s:integer; <br />\r\nBEGIN <br />\r\nclrscr; <br />\r\ns:=0; <br />\r\n<br />\r\nwrite(So cong trinh can tinh: );readln(n); <br />\r\nfor i:=1 to n do <br />\r\nbegin <br />\r\nwrite( Cong trinh thu ,i, : );readln(a[i]); <br />\r\ns:=s+a[i]; <br />\r\nend; <br />\r\nmax:=a[1]; <br />\r\nmin:=a[1]; <br />\r\nfor i:=2 to n do <br />\r\nbegin <br />\r\nif maxa[i] then min:=a[i]; <br />\r\nend; <br />\r\n<br />\r\nwriteln( Trung binh : ,s/n:4:2); <br />\r\nwriteln( Cao nhat: ,max); <br />\r\nwriteln( Thap nhat: ,min); <br />\r\nreadln; <br />\r\nEND. <br />\r\n<br />\r\n//P/s: mình chưa test thử, không biết yêu cầu đưa ra công trình cao nhất thấp nhất của bạn là đưa ra số thứ tự công trình hay sản lượng nên mình chỉ in ra sản lượng cao nhất thấp nhâts thôi, có gì bạn chỉnh lại nhé', '15:49:04 | 17-12-2015 ');
INSERT INTO `data` VALUES(71, 'trungnhan', 'Dự án chặt cây rừng để xây dựng trụ sở la co nen hay ko? cho em mot loi giai thich voi.cam on ak!!! em đang cần gấp lắm?', 'Dự án chặt cây rừng để xây dựng trụ sở la co nen hay ko? cho em mot loi giai thich voi.cam on ak!!! em đang cần gấp lắm?<br />\r\n', '15:49:49 | 17-12-2015 ');
INSERT INTO `data` VALUES(73, 'trungnhan', 'Vì sao ban đêm không nên để cây xanh và hoa ở trong ngủ đóng kín?', 'Mình học lớp 6, cô bảo mình trả lời câu hỏi này. vì sao không nên để cây xanh hoặc hoa ở trong phòng ngủ vậy mọi người, trả lời giùm mình nhé<br />\r\n<br />\r\nTrả lời:<br />\r\nkhông nên để cây xanh trong phòng ngủ vì cây xanh quang hợp vào ban ngày là quá trình lấy khí các bô níc và thải ra khí oxi . oxi là chất không thể thiếu của con người <br />\r\n.Cây xanh hô hấp vào ban đêm là quá trình lấy khí oxi và thải ra khí các bonic nên để cây xanh trong phòng ngủ sẽ làm cho con người cảm thấy ngột ngạt vì cây xanh lấy đi khí oxi<br />\r\n', '15:51:00 | 17-12-2015 ');
