INSERT INTO `data` VALUES(294, 'trungnhan', ' Bạn có thể kể ra ba ngày liên tiếp mà không có tên là thứ hai, thứ ba, thứ tư, thứ năm, thứ sáu, thứ bảy, chủ nhật?', ' Bạn có thể kể ra ba ngày liên tiếp mà không có tên là thứ hai, thứ ba, thứ tư, thứ năm, thứ sáu, thứ bảy, chủ nhật?<br />\r\nĐáp án: Hôm qua, hôm nay và ngày mai.<br />\r\n<br />\r\n', '13:34:04 | 05-02-2016 ');
