INSERT INTO `data` VALUES(305, 'trungnhan', ' 2 người: 1 lớn, 1 bé đi lên đỉnh một quả núi. Người bé là con của người lớn, nhưng người lớn lại không phải cha của ngư', ' 2 người: 1 lớn, 1 bé đi lên đỉnh một quả núi. Người bé là con của người lớn, nhưng người lớn lại không phải cha của người bé, hỏi người lớn là ai?<br />\r\n<br />\r\nĐáp án: Mẹ.<br />\r\n<br />\r\n', '13:36:00 | 05-02-2016 ');
