INSERT INTO `data` VALUES(307, 'trungnhan', 'Ai cũng biết đỉnh núi Everest cao nhất thế giới, vậy trước khi đỉnh Everest được khám phá, đỉnh núi nào cao nhất thế giớ', 'Ai cũng biết đỉnh núi Everest cao nhất thế giới, vậy trước khi đỉnh Everest được khám phá, đỉnh núi nào cao nhất thế giới?<br />\r\nĐáp án: Everest.<br />\r\n<br />\r\n', '13:36:23 | 05-02-2016 ');
