INSERT INTO `data` VALUES(336, 'trungnhan', 'Hãy đối lại một câu trái nghĩa với câu này:”đất lành chim đậu”', 'Hãy đối lại một câu trái nghĩa với câu này:”đất lành chim đậu”<br />\r\nĐÁP ÁN: Đất giữ chim đi.<br />\r\n', '18:17:48 | 05-02-2016 ');
