INSERT INTO `data` VALUES(356, 'trungnhan', 'Hôn con heo trong nhà gọi là -> Hôn thú  Mong được hôn gọi là -> Cầu hôn  Vừa mới hôn gọi là -> Tân hôn  Hôn thêm cái nữ', 'Hôn con heo trong nhà gọi là -> Hôn thú <br />\r\nMong được hôn gọi là -> Cầu hôn <br />\r\nVừa mới hôn gọi là -> Tân hôn <br />\r\nHôn thêm cái nữa gọi là -> Tái hôn <br />\r\nĐang hôn mà bị đẩy ra gọi là -> Từ hôn <br />\r\nKhông cho mà cứ hôn gọi là -> Ép hôn <br />\r\nHẹn sẽ hôn gọi là -> Hứa hôn <br />\r\nVua hôn gọi là -> Hoàng hôn <br />\r\nHôn chia tay gọi là -> Ly hôn <br />\r\nVừa hôn vừa ngửi gọi là -> Vị hôn <br />\r\nHôn vào không trung gọi là -> Hôn gió <br />\r\nHôn trong mơ gọi là -> Hôn ước <br />\r\nHôn mà mà quá sớm thì gọi là -> Tảo hôn <br />\r\nRất thích hôn gọi là -> Kết hôn <br />\r\nHôn mà bị hôn lại gọi là -> Đính hôn ', '18:27:09 | 05-02-2016 ');
INSERT INTO `data` VALUES(357, 'trungnhan', 'Trận chung kết của 1 cuộc thi vắt sữa bò (ai vắt được nhiêu thìthắng): người đàn bà vắt được 15 lít, người đàn ông vắt đ', 'Trận chung kết của 1 cuộc thi vắt sữa bò (ai vắt được nhiêu thìthắng): người đàn bà vắt được 15 lít, người đàn ông vắt được 5 lít. Hỏisao người đàn ông thắng? <br />\r\nTL: Người đàn bà vắt sữa con bò cái, còn người đàn ông vắt sữa con bò đực. <br />\r\n', '18:27:24 | 05-02-2016 ');
