INSERT INTO `data` VALUES(385, 'trungnhan', 'Neil Armstrong', 'Neil Armstrong là một phi hành gia người Mỹ, người đầu tiên đặt chân lên Mặt Trăng ngày 20 tháng 7 năm 1969, trong chuyến du hành trên tàu Apollo 11 cùng Buzz Aldrin & Michael Collins.<br />\r\n[img]http://a2.files.biography.com/image/upload/c_fill,cs_srgb,dpr_1.0,g_face,h_300,q_80,w_300/MTIwNjA4NjMzNDA3NTcxNDY4.jpg[/img]<br />\r\nSinh: 5 tháng 8, 1930, Wapakoneta, Ohio, Hoa Kỳ<br />\r\nMất: 25 tháng 8, 2012, Cincinnati, Ohio, Hoa Kỳ<br />\r\nChuyến bay không gian: Gemini 8, Apollo 11<br />\r\nVợ/chồng: Carol Held Knight (kết hôn 1994–2012), Janet Shearon (kết hôn 1956–1994)<br />\r\nBố mẹ: Stephen Armstrong, Viola Armstrong<br />\r\nAnh chị em: Dean Armstrong, June Armstrong', '06:34:45 | 06-02-2016 ');
