INSERT INTO `data` VALUES(387, 'trungnhan', 'Archimedes', '[img]data:image/jpeg;base64,[/img]<br />\r\nArchimedes<br />\r\nNhà toán học<br />\r\n<br />\r\nArchimedes của Syracuse phiên âm tiếng Việt: Ác-si-mét; khoảng là một nhà toán học, nhà vật lý, kỹ sư, nhà phát minh, và một nhà thiên văn học người Hy Lạp. <br />\r\nSinh: Siracusa, Ý<br />\r\nMất: Siracusa, Ý<br />\r\nBố mẹ: Phidias', '07:48:56 | 06-02-2016 ');
INSERT INTO `data` VALUES(388, 'trungnhan', 'Năm 7 tuổi ông học khoa học tự nhiên, triết học, văn học. Năm 11 tuổi ông du học ở Ai Cập, trở thành học sinh của nhà To', 'Năm 7 tuổi ông học khoa học tự nhiên, triết học, văn học. Năm 11 tuổi ông du học ở Ai Cập, trở thành học sinh của nhà Toán học Euclides. Câu nói nổi tiếng của ông: "Hãy cho tôi một điểm tựa, tôi sẽ nâng bỗng cả trái đất". Bạn có biết ông là ai ?<br />\r\nĐáp án : Archimedes', '07:56:40 | 06-02-2016 ');
