INSERT INTO `data` VALUES(395, 'trungnhan', 'Thành phố nào nổi tiếng với những hành lang có mái che, tháp chuông đồng hồ. Đây cũng là nơi albert Eistein đã phát triể', 'Thành phố nào nổi tiếng với những hành lang có mái che, tháp chuông đồng hồ. Đây cũng là nơi albert Eistein đã phát triển thuyết tương đối?<br />\r\nĐáp án: Thành phố Berne (Swiss)', '08:06:00 | 06-02-2016 ');
INSERT INTO `data` VALUES(396, 'trungnhan', 'Ông sử dụng con lắc để chế tạo đồng hồ, ông cũng tìm ra rằng tốc độ rơi không phụ thuộc vào trọng lượng. Bạn cho biết ôn', 'Ông sử dụng con lắc để chế tạo đồng hồ, ông cũng tìm ra rằng tốc độ rơi không phụ thuộc vào trọng lượng. Bạn cho biết ông là ai ?<br />\r\nĐáp án : Galilee', '08:08:49 | 06-02-2016 ');
