INSERT INTO `data` VALUES(398, 'trungnhan', 'Niels Bohr', '<br />\r\nNiels Bohr<br />\r\nNhà vật lý học<br />\r\n<br />\r\n[img]http://designkompany.com/slate/wp-content/uploads/2015/12/39d36367-3033-46fe-82b9-000b6e644b7a.jpg[/img]<br />\r\n<br />\r\nNiels Henrik David Bohr là nhà vật lý học người Đan Mạch với những đóng góp nền tảng về lý thuyết cấu trúc nguyên tử và cơ học lượng tử sơ khai, nhờ đó mà ông nhận Giải Nobel Vật lý năm 1922.<br />\r\nSinh: 7 tháng 10, 1885, Copenhagen, Đan Mạch<br />\r\nMất: 18 tháng 11, 1962, Copenhagen, Đan Mạch<br />\r\nVợ/chồng: Margrethe Nørlund (kết hôn 1912–1962)<br />\r\nBố mẹ: Christian Bohr, Ellen Adler Bohr<br />\r\nCon: Aage Niels Bohr, Ernest Bohr, Hans Henrik Bohr, Erik Bohr, Harald Bohr, Christian Bohr<br />\r\nGiải thưởng: Giải Nobel Vật lý, Huy chương Copley', '08:11:17 | 06-02-2016 ');
