INSERT INTO `data` VALUES(402, 'trungnhan', 'Ông là nhà Vật lý lỗi lạc của nước Pháp đã cùng với vợ mình khám phá ra hiện tượng phóng xạ, tìm ra chất radium. Ông là ', 'Ông là nhà Vật lý lỗi lạc của nước Pháp đã cùng với vợ mình khám phá ra hiện tượng phóng xạ, tìm ra chất radium. Ông là ai ?<br />\r\nĐáp án : Pierre Currie', '10:14:05 | 06-02-2016 ');
INSERT INTO `data` VALUES(403, 'trungnhan', 'Pierre Curie', 'Pierre Curie<br />\r\nNhà vật lý<br />\r\nPierre Curie là một nhà vật lý người Pháp, người tiên phong trong lĩnh vực tinh thể học, từ tính, hiện tượng áp điện và hiện tượng phóng xạ.<br />\r\n[img]https://upload.wikimedia.org/wikipedia/commons/d/db/Pierre_Curie_by_Dujardin_c1906.jpg[/img]<br />\r\n<br />\r\nSinh: 15 tháng 5, 1859, Paris, Pháp<br />\r\n<br />\r\nMất: 19 tháng 4, 1906, Paris, Pháp<br />\r\n<br />\r\nVợ/chồng: Marie Curie (kết hôn 1895–1906)<br />\r\n<br />\r\nBố mẹ: Sophie-Claire Depouilly Curie, Eugène Curie<br />\r\n<br />\r\nCon: Irène Joliot-Curie, Ève Curie<br />\r\nĐã khám phá: Radi, Poloni', '10:15:21 | 06-02-2016 ');
