INSERT INTO `data` VALUES(408, 'trungnhan', 'Ernest Rutherford', 'Ernest Rutherford<br />\r\nNhà vật lý<br />\r\nErnest Rutherford là một nhà vật lý người New Zealand hoạt động trong lĩnh vực phóng xạ và cấu tạo nguyên tử.<br />\r\n<br />\r\n[img]http://www.sciencekids.co.nz/images/pictures/scientists/ernestrutherford.jpg[/img]<br />\r\n<br />\r\nSinh: 30 tháng 8, 1871, Brightwater, New Zealand<br />\r\n<br />\r\nMất: 19 tháng 10, 1937, Cambridge, Vương quốc Liên hiệp Anh và Bắc Ireland<br />\r\n<br />\r\nVợ/chồng: Mary Georgina Newton (kết hôn 1900–1937)<br />\r\n<br />\r\nBố mẹ: James Rutherford, Martha Thompson<br />\r\n<br />\r\nCon: Eileen Mary Rutherford Fowler<br />\r\n<br />\r\nGiải thưởng: Giải Nobel Hóa học, Huy chương Matteucci, Huy chương Copley, Huy chương Franklin', '10:22:35 | 06-02-2016 ');
