INSERT INTO `data` VALUES(447, 'trungnhan', 'Cắm vào run rẩy toàn thân Rút ra nước chảy từ chân xuống sàn Hỡi chàng công tử giàu sang Cắm vào xin chớ vội vàng rút ra', 'Cắm vào run rẩy toàn thân<br />\r\nRút ra nước chảy từ chân xuống sàn<br />\r\nHỡi chàng công tử giàu sang<br />\r\nCắm vào xin chớ vội vàng rút ra!<br />\r\n<br />\r\nĐáp án: Đó là cái tủ lạnh!<br />\r\n', '05:45:48 | 07-02-2016 ');
