INSERT INTO `data` VALUES(488, 'trungnhan', 'Nếu bạn có thể sống đến 90 tuổi và giữ được hoặc tinh thần hoặc thể xác ở tuổi 30 cho 60 năm cuối cùng của đời mình bạn', 'Nếu bạn có thể sống đến 90 tuổi và giữ được hoặc tinh thần hoặc thể xác ở tuổi 30 cho 60 năm cuối cùng của đời mình, bạn sẽ chọn cái nào?', '09:11:05 | 07-02-2016 ');
INSERT INTO `data` VALUES(489, 'trungnhan', 'Bạn có bất kì linh cảm bí mật nào về cách mà mình sẽ chết?', 'Bạn có bất kì linh cảm bí mật nào về cách mà mình sẽ chết?', '09:11:09 | 07-02-2016 ');
INSERT INTO `data` VALUES(490, 'trungnhan', 'Nêu lên 3 thứ là điểm chung của bạn và người bạn của mình.', 'Nêu lên 3 thứ là điểm chung của bạn và người bạn của mình.', '09:11:15 | 07-02-2016 ');
