INSERT INTO `data` VALUES(493, 'trungnhan', 'Dành ra 4 phút để nói với người bạn của mình về một câu chuyện trong cuộc đời của bạn càng chi tiết càng tốt.', 'Dành ra 4 phút để nói với người bạn của mình về một câu chuyện trong cuộc đời của bạn, càng chi tiết càng tốt.', '09:11:32 | 07-02-2016 ');
INSERT INTO `data` VALUES(494, 'trungnhan', 'Nếu bạn thức dậy vào sáng mai và có thể có một trình độ/ hiểu biết hoặc một khả năng bất kì đó sẽ là điều gì?', 'Nếu bạn thức dậy vào sáng mai và có thể có một trình độ/ hiểu biết hoặc một khả năng bất kì, đó sẽ là điều gì?', '09:11:37 | 07-02-2016 ');
INSERT INTO `data` VALUES(495, 'trungnhan', 'Nếu một quả cầu thủy tinh có thể nói sự thật về bản thân bạn cuộc đời của bạn và tương lai hay bất kì thứ gì khác bạn ', 'Nếu một quả cầu thủy tinh có thể nói sự thật về bản thân bạn, cuộc đời của bạn và tương lai hay bất kì thứ gì khác, bạn sẽ muốn biết điều gì?', '09:11:50 | 07-02-2016 ');
INSERT INTO `data` VALUES(496, 'trungnhan', 'Có thứ gì mà bạn đã mơ ước được thực hiện từ lâu lắm rồi? Tại sao bạn vẫn chưa hoàn thành nó?', 'Có thứ gì mà bạn đã mơ ước được thực hiện từ lâu lắm rồi? Tại sao bạn vẫn chưa hoàn thành nó?', '09:11:58 | 07-02-2016 ');
