INSERT INTO `data` VALUES(500, 'trungnhan', 'Kỉ niệm kinh khủng nhất của bạn là gì?', 'Kỉ niệm kinh khủng nhất của bạn là gì?', '09:21:21 | 07-02-2016 ');
INSERT INTO `data` VALUES(501, 'trungnhan', 'Nếu bạn biết rằng trong 1 năm nữa bạn sẽ bất ngờ chết bạn sẽ thay đổi điều gì trong cách sống của mình hiện nay? Tại s', 'Nếu bạn biết rằng trong 1 năm nữa, bạn sẽ bất ngờ chết, bạn sẽ thay đổi điều gì trong cách sống của mình hiện nay? Tại sao?', '09:21:26 | 07-02-2016 ');
INSERT INTO `data` VALUES(502, 'trungnhan', 'Tình bạn có ý nghĩ như thế nào với bạn?', 'Tình bạn có ý nghĩ như thế nào với bạn?', '09:21:32 | 07-02-2016 ');
