INSERT INTO `data` VALUES(504, 'trungnhan', ' Lần lượt từng bạn chia sẻ một tính cách tốt của người bạn mình. Tổng cộng 5 điều.', ' Lần lượt từng bạn chia sẻ một tính cách tốt của người bạn mình. Tổng cộng 5 điều.', '09:21:44 | 07-02-2016 ');
INSERT INTO `data` VALUES(505, 'trungnhan', 'Gia đình bạn gần gũi và ấm cúng đến mức nào? Bạn có thấy là thời thơ ấu của mình hạnh phúc hơn những người khác?', 'Gia đình bạn gần gũi và ấm cúng đến mức nào? Bạn có thấy là thời thơ ấu của mình hạnh phúc hơn những người khác?', '09:21:54 | 07-02-2016 ');
