INSERT INTO `data` VALUES(507, 'trungnhan', 'Hoàn tất 3 câu “Chúng tôi… ” thật lòng. Ví dụ: “Chúng ta đều ở trong căn phòng này cảm thấy….".', 'Hoàn tất 3 câu “Chúng tôi… ” thật lòng. Ví dụ: “Chúng ta đều ở trong căn phòng này, cảm thấy….".', '09:22:15 | 07-02-2016 ');
