INSERT INTO `data` VALUES(509, 'trungnhan', 'Nếu bạn sắp trở thành bạn thân với bạn của mình xin hãy chia sẻ điều quan trọng cho cô ấy/ anh ấy biết.', 'Nếu bạn sắp trở thành bạn thân với bạn của mình, xin hãy chia sẻ điều quan trọng cho cô ấy/ anh ấy biết.', '09:22:27 | 07-02-2016 ');
INSERT INTO `data` VALUES(510, 'trungnhan', 'Nói với người bạn của mình về điều mà bạn thích ở họ; hãy thật chân thành trong lúc này nói những điều bạn chưa hẳn đã ', 'Nói với người bạn của mình về điều mà bạn thích ở họ; hãy thật chân thành trong lúc này, nói những điều bạn chưa hẳn đã nói với người mà bạn vừa gặp.', '09:22:33 | 07-02-2016 ');
INSERT INTO `data` VALUES(511, 'trungnhan', 'Chia sẻ với người bạn của mình về một khoảnh khắc đáng xấu hổ trong cuộc đời mình.', 'Chia sẻ với người bạn của mình về một khoảnh khắc đáng xấu hổ trong cuộc đời mình.', '09:22:39 | 07-02-2016 ');
