INSERT INTO `data` VALUES(515, 'trungnhan', 'Nếu bạn phải chết vào tối nay mà không có cơ hội được trò chuyện với ai cả điều gì mà bạn không nói được khiến bạn hối ', 'Nếu bạn phải chết vào tối nay mà không có cơ hội được trò chuyện với ai cả, điều gì mà bạn không nói được khiến bạn hối hận nhất? Tại sao bạn vẫn chưa nói điều đó ra?', '09:23:04 | 07-02-2016 ');
INSERT INTO `data` VALUES(516, 'trungnhan', 'Nhà của bạn chứa tất cả những thứ của bạn bị cháy. Sau khi cứu tất cả những người bạn yêu thương bạn còn đủ thời gian ', 'Nhà của bạn, chứa tất cả những thứ của bạn bị cháy. Sau khi cứu tất cả những người bạn yêu thương, bạn còn đủ thời gian để an toàn mang ra 1 vật dụng nào đó. Đó sẽ là thứ gì? Tại sao?', '09:23:11 | 07-02-2016 ');
INSERT INTO `data` VALUES(517, 'trungnhan', 'Trong tất cả những thành viên trong gia đình mình thì sự ra đi của ai khiến bạn phiền muộn nhất? Tại sao?', 'Trong tất cả những thành viên trong gia đình mình thì sự ra đi của ai khiến bạn phiền muộn nhất? Tại sao?', '09:23:17 | 07-02-2016 ');
INSERT INTO `data` VALUES(518, 'trungnhan', 'Chia sẻ một vấn đề cá nhân và hỏi xin lời khuyên từ người bạn của mình về cách mà anh ấy/ cô ấy sẽ xử lý nó. Và thêm cả ', 'Chia sẻ một vấn đề cá nhân và hỏi xin lời khuyên từ người bạn của mình về cách mà anh ấy/ cô ấy sẽ xử lý nó. Và thêm cả hỏi bạn của mình về cách bạn cảm nhận về vấn đề mà bạn đã chọn.', '09:23:25 | 07-02-2016 ');
