INSERT INTO `data` VALUES(589, 'trungnhan', '[Công thức] Cách tính diện tích và chu vi hình thoi', 'Hình thoi là gì?<br />\r\n<br />\r\nHình thoi là một tứ giác có 4 cạnh bằng nhau<br />\r\n[img]http://timcachlam.com/wp-content/uploads/2015/11/cong-tinh-dien-tich-va-chu-vi-hinh-thoi.jpg[/img]<br />\r\nGóc học tập dành cho các bạn học sinh đang theo học nếu quên công thức nào nhớ ghé vào website timcachlam.com để xem lại nhé, hôm nay là công thức tính diện tích và chu vi hình thoi<br />\r\n<br />\r\nCông thức tính diện tích hình thoi<br />\r\n[img]http://timcachlam.com/wp-content/uploads/2015/11/cong-thuc-tinh-dien-tich-hinh-thoi.png[/img]<br />\r\nd1 là đường chéo<br />\r\nd2 là đường chéo<br />\r\n<br />\r\nCông thức tính chu vi hình thoi<br />\r\n<br />\r\n[img]http://timcachlam.com/wp-content/uploads/2015/11/cong-thuc-tinh-chu-vi-hinh-thoi.png[/img]<br />\r\n<br />\r\nộ dài một cạnh của hình thoi<br />\r\nChúc các bạn học sinh đang theo các lớp học có thể nhớ được những kiến thức cơ bản này.', '17:32:06 | 07-02-2016 ');
